/*获取一个可以用的房间*/
RoomManager.getAvailableRoom = function (peopleArr,callback) {
    var sql = "SELECT * FROM rooms WHERE status = 0  ORDER BY rand() LIMIT 1";
    db.query(sql,null,function (error,result) {
        if (error) {
            console.log('[SELECT ERROR] -',error.message);
            callback(false,null);
        }
        //console.log("用空余的房间")
        //处理
        //console.log(result);
        if (result.length > 0) {
            var data = result[0];
            var obj = new RoomManager();
            obj.id = data['id'];
            obj.roomId = data['roomId'];
            obj.status = data['status'];
            //改变状态
            obj.setRoomStatus(1);
            callback(true,obj,peopleArr);
        }
    });
};

/*创建代开房列表*/
RoomManager.createDaiKaiFang = function(roomId,createUserId,gameType,callback)
{
    var sql = "INSERT INTO daikairooms(roomId,createUserId,type) VALUES(?,?,?)";
    var sqlParam = [roomId,createUserId,gameType];
    db.query(sql,sqlParam,function (error,result) {
        if (error) {
            console.log('[INSERT/UPDATE DAI KAI ERROR] - ',error.message);
            return;
        }
        callback(true);
    });
}

/*获取代开房房间列表*/
RoomManager.getDaiKaiFang = function(createUserId,callback)
{
    var sql = "SELECT * FROM daikairooms WHERE createUserId = ?";
    var sqlParam = [createUserId];
    console.log("查询的createUserId====" + createUserId);
    db.query(sql,sqlParam,function (error,result) {
        if (error) {
            console.log('[SELECT ERROR] -',error.message);
            callback(false,null);
        }
        //处理
        if (result.length > 0) {
            var list = [];
            console.log("结果："+result.length);
            for(var i=0;i<result.length;i++)
            {
                var obj = {};
                var data = result[i];
                obj.gameType = data['type'];
                obj.roomId = data['roomId'];
                obj.createUserId = data['createUserId'];
                list.push(obj);
            }
            callback(true,list);
        }
    });
}


